--Criando as Respostas--
Respostas = {}
table.insert(Respostas, "A pesquisa deve ter alguma relevância para a sociedade para que seja de algum modo útil para a humanidade.")
table.insert(Respostas, "A pesquisa só deve ser realizada se esse for o único modo de adquirir o conhecimento.")
table.insert(Respostas, "Toda pesquisa envolvendo seres humanos deve atender, obrigatoriamente, aos fundamentos éticos e científicos referentes a ela.")
